INSERT INTO `meituan_dongwuyuan` VALUES ('9131', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '春节期间还是有很多人去的  开车路过放养的动物身边还是很欢乐的  喂养动物也很有意思  里面还有一头脾气暴躁的矮马王  不给它吃 给别的马吃还会生气  很可爱~还有大象  爱吃香蕉  喂它甘蔗它会接过丢掉  动物的食物园里都有卖  十块钱三袋 也不贵  大家千万不要自己带东西 随便喂食动物  适合一家老少去玩玩', '2015-03-02 22:22:12', '-2109626031:http://p0.meituan.net/shaitu/4f1fba827802bf8bb3d1f71076b536cb80664.jpg;\n-2109671478:http://p0.meituan.net/shaitu/48222b66b48941bc8dc39f653682b42879578.jpg;\n-2109633965:http://p1.meituan.net/shaitu/8b67561b83310f92709f1399114106ce92206.jpg;\n-2109622019:http://p0.meituan.net/shaitu/b74c1a7695af5ba1d82872ae6866e67e169556.jpg;\n-2109630713:http://p0.meituan.net/shaitu/99b4ff4d8c111d48ae147c684706cfd7135275.jpg;\n', 'None');
