INSERT INTO `meituan_dongwuyuan` VALUES ('9141', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺好的！管理动物的工作人员态度都不错，唯独管理猴子的大叔，我们不愿让孩子买东西给猴子吃，怕猴子不小心伤了孩子，他很是不高兴，一直发问：“你们带小孩来，不让孩子高兴，走那么快干什么？哪里有怎么好的地方，零距离接触动物？”我们家长有家长的顾虑，买不买东西喂猴子是我们的事，不需要这样说话，影响心情！', '2015-03-02 22:22:07', '-2109864977:http://p1.meituan.net/shaitu/277c4a4c93779a2d8d8a2e74fef0c64b125479.jpg;\n-2109856374:http://p1.meituan.net/shaitu/2775d05610c974371f2a8348bca747cc67244.jpg;\n-2109822414:http://p1.meituan.net/shaitu/bd51688c5ec13ccbf3331c82d8a6c65b88646.jpg;\n', 'None');
