INSERT INTO `meituan_dongwuyuan` VALUES ('9146', 'wYe330954717', '83303130', '', '丛海口过去三十公里，提醒一下没车的朋友，必须包的士或者租车，因为野生猛兽区只能坐车观看，中线的路修好了，过去还算方便，比起市内的金牛岭动物园好多了，但是动物种类多不了几种，不满意的是海南人没半价优惠，70块团购可以去南山了', '2015-03-02 22:21:54', '-2113657050:http://p0.meituan.net/shaitu/f51ee5227e7e40dcb555274b32bd6be4131749.jpg;\n-2113652496:http://p1.meituan.net/shaitu/f1f1722ee062955fe30cad5bfed364c3182710.jpg;\n-2113670342:http://p0.meituan.net/shaitu/d14bb767296a2d769915473ada41894c116566.jpg;\n', 'None');
