INSERT INTO `meituan_dongwuyuan` VALUES ('9338', '繁花若姒锦', '35749935', '', '动物园的环境不错，但是设施还差点，有地理位置的优势，就是一个森林改造的，树木很多，生态不错，就是动物太少了，回去之后身上出现了好几处红疙瘩，可能是小虫咬的', '2015-02-16 14:59:39', '', 'None');
