INSERT INTO `meituan_dongwuyuan` VALUES ('9505', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '动物比较少，大象只剩一头了，还很瘦，看着有点可怜。野外区不错，熊和老虎养的挺好。唯一生气的是在大象那买了3袋喂食的但去到别的动物那竟然不给喂，同样是番薯，说是私人承包的', '2015-01-05 12:22:25', '', 'None');
