INSERT INTO `meituan_dongwuyuan` VALUES ('9564', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '去的时候天冷人少，步行区树林里感觉好阴森，车行区很棒，有了不一样的体验，野外动物都比较活泼，熊还冲过来趴在车窗上，直到出来了还心惊肉跳，就是路还没修好，等修好后再去', '2014-12-17 17:37:33', '-2073022807:http://p1.meituan.net/shaitu/060e7517a00ef29185dec309cfd7ffb2130410.jpg;\n-2073002956:http://p1.meituan.net/shaitu/a96b2e45f296b2531b8086afc4cdb33f137669.jpg;\n-2073016610:http://p0.meituan.net/shaitu/7f6af32931e722d2a3eccbfac069df7a100718.jpg;\n', 'None');
