INSERT INTO `meituan_dongwuyuan` VALUES ('9617', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '美团57元门票还是挺值的，就是路太不好走。去的时候走那条路在修，回来走的那条路也在修。都是从那些村子里问别人跑的小道来回的，建议大家还是等路修好以后再去，路修得真是太慢了，至少都要明年以后', '2014-11-29 05:59:30', '-2118194474:http://p1.meituan.net/shaitu/528715ecb2d2fd8102f748305a8a3c4661567.jpg;\n-2118192482:http://p1.meituan.net/shaitu/0eed4bd48fb5b632b32cad8a4304d1a145893.jpg;\n-2118168984:http://p0.meituan.net/shaitu/826ad43d9687d1325d61a79572878338102851.jpg;\n', 'None');
