INSERT INTO `meituan_dongwuyuan` VALUES ('9748', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '感觉环境还好，去哪里心情很愉快的，很好奇心的去观看动物活泼可爱的一目', '2014-11-19 01:19:02', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9749', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺好玩，园区景色不错，像进了天然大氧吧，植物挺有特色，动物也挺干净有活力', '2014-11-18 23:04:33', '-2097724663:http://p0.meituan.net/shaitu/__43474954__2175305.jpg;\n-2097784128:http://p0.meituan.net/shaitu/__43474918__8510905.jpg;\n-2097762629:http://p0.meituan.net/shaitu/__43474951__4644085.jpg;\n-2097709356:http://p1.meituan.net/shaitu/__43474912__1766218.jpg;\n-2097728195:http://p0.meituan.net/shaitu/__43474920__5767142.jpg;\n-2097747625:http://p1.meituan.net/shaitu/__43474914__9146617.jpg;\n-2097784096:http://p0.meituan.net/shaitu/__43474909__6913006.jpg;\n', 'None');
