INSERT INTO `meituan_dongwuyuan` VALUES ('9752', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '随手拍吃喝玩乐，海南动植物园值得一去！！！', '2014-11-18 17:31:51', '-2099422407:http://p1.meituan.net/shaitu/__42970150__2099290.jpg;\n-2099509137:http://p1.meituan.net/shaitu/__42970157__5194166.jpg;\n', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9753', '笨鸟9527', '95294018', '', '带孩子去玩的，很开心。动物都过得不错，要是品种再多一些就更好了！', '2014-11-18 16:03:35', '', 'None');
