INSERT INTO `meituan_dongwuyuan` VALUES ('9765', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '好玩', '2014-11-18 07:31:22', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9766', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '总的来说是不错的，小孩玩得很开心！美中不足的是美团团购不给电子导游机！谢谢电话给我指路的美女！', '2014-11-18 07:24:56', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9767', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '永兴路段已成一条河而不是路了，午餐菜上半天饭还没上桌而且又贵又无味，动物还挺多，不过有很多毛都掉光了，没有长颈鹿.', '2014-11-18 05:43:44', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9768', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '去过两次了，路太不好走了，赶上下雨天更是！', '2014-11-18 04:49:49', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('9769', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '孩子很高兴，就是里面消费贵了点。', '2014-11-18 03:47:02', '-2119988176:http://p0.meituan.net/shaitu/__41823357__6291313.jpg;\n-2119955285:http://p0.meituan.net/shaitu/__41823352__2038305.jpg;\n-2119981350:http://p1.meituan.net/shaitu/__41823343__8035638.jpg;\n-2119948396:http://p1.meituan.net/shaitu/__41823361__4420187.jpg;\n-2119948739:http://p0.meituan.net/shaitu/__41823348__4001749.jpg;\n', 'None');
