INSERT INTO `meituan_dongwuyuan` VALUES ('9867', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '总体还行，晴天过去灰很大。冲着长颈鹿去的，听工作人员说长颈鹿挂了。大象只有一只。小的玩的很开心。总体还不错。', '2014-11-14 22:20:57', '', 'None');
