INSERT INTO `meituan_dongwuyuan` VALUES ('9961', '汀上白沙1999', '56328718', '', '真的很好玩啊，孩子们最喜欢的就是猴山，可以那么近的喂猴子，去过野生动物园就不愿意去普通动物园了。', '2014-11-11 11:34:11', '', 'None');
