INSERT INTO `meituan_dongwuyuan` VALUES ('10236', '周周660', '28348071', '', '餐厅环境很别具一格，玻璃都是透明的，可以很清楚看到外面的树木禽鸟，餐厅内更有小桥流水，鱼儿畅游。宝宝非常喜欢这个环境，玩的很开心。套餐内菜的数量不少，就是份量稍少。不过对于这个价格来说，性价比非常高，去玩的人就餐的最好选择！', '2014-08-25 23:29:32', '-1941430158:http://p1.meituan.net/shaitu/1fffaecf464b0d90bc248aee16f6aec6148074.jpg;\n-1940694228:http://p0.meituan.net/shaitu/6312a827cdfe05eaeb465edac11f67f5100597.jpg;\n-1941267226:http://p1.meituan.net/shaitu/c06495f802fd56340055f09a44d26f23119113.jpg;\n', 'None');
