INSERT INTO `meituan_dongwuyuan` VALUES ('10238', '_qqsxw1408719912', '106869333', '', '去动物园的路基本修好了，只有一段难走一点点。动物园很不错，有很多猛兽，老虎狮子熊大象等等，分车行区与步行区，都挺好。特别是能见到成活率50万分之一的虎狮兽，狮虎兽。动物养得挺好，壮壮的。可惜没有袋鼠和长颈鹿。可能台风的关系，稀奇的植物少了点。', '2014-08-25 11:32:23', '-2034740773:http://p1.meituan.net/shaitu/b598b1bc652fde99fc04000d2aa5731e124305.jpg;\n-2034617180:http://p0.meituan.net/shaitu/7310b30574e794ccea2792f4b16efc22127201.jpg;\n-2034826610:http://p0.meituan.net/shaitu/4c7fa3c9c44fcf4f2bb946114a30e22d127088.jpg;\n-2034894954:http://p1.meituan.net/shaitu/ba47ec3e74ea90c0b4b988ccc1403d03131816.jpg;\n-2034826594:http://p1.meituan.net/shaitu/877b22f0a73c983339502233cc806f95157635.jpg;\n-2034894938:http://p1.meituan.net/shaitu/ac42cf13f4abd5b9c3cbc8429aba283e174468.jpg;\n-2034812129:http://p0.meituan.net/shaitu/aa397d2a5150b58abeacddc475d86396181968.jpg;\n-2034658328:http://p1.meituan.net/shaitu/45e948b8ac9f0644f328ab5bf0d116a2152872.jpg;\n', 'None');
