INSERT INTO `meituan_dongwuyuan` VALUES ('10571', 'NvT50280990', '67198839', '', '感觉还是蛮不错的，让2岁的宝宝见识到了动画片里面的真家伙：什么包包大人，猴王吉吉， 毛毛，之类，就是没有看到长颈鹿，有点失望。 对了 去的路太难走了点，路窄而且又很多大车装着沙子石头啥的，挺考验车技的，幸好不是我自己开车。', '2014-05-15 08:57:54', '', 'None');
