INSERT INTO `meituan_dongwuyuan` VALUES ('10624', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '周一去得，没什么人阿，动物不多，长颈鹿没有，据说死了。饭菜还算新鲜，植物很茂盛，像到了热带雨林一样，空气很好的。补充一句，我是坐旅游2路去得，票价20，在万绿园出发，票价很值，因为路超级难走的，千万不要自己开车从海口去阿，轮胎要废掉的。', '2014-04-28 15:19:24', '-1932336565:http://p0.meituan.net/shaitu/__41671054__5912423.jpg;\n-1932297033:http://p0.meituan.net/shaitu/__41671121__9537008.jpg;\n-1932218192:http://p1.meituan.net/shaitu/__41671064__6004275.jpg;\n-1932297017:http://p0.meituan.net/shaitu/__41671082__6650201.jpg;\n-1932921886:http://p1.meituan.net/shaitu/__41671106__2815742.jpg;\n-1932337921:http://p1.meituan.net/shaitu/__41671075__2783502.jpg;\n-1932336549:http://p0.meituan.net/shaitu/__41671112__9615763.jpg;\n-1932296985:http://p0.meituan.net/shaitu/__41671139__8733852.jpg;\n', 'None');
