INSERT INTO `meituan_dongwuyuan` VALUES ('10637', 'starrainwqz', '52690175', '', '非常自然的景点。适合于亲子游。能把车直接开到景区内的猛兽区，刚开始还有点怕怕的，不过一会就适用了。还亲自玩了用甘蔗和大象拔河的游戏，真的很不错。小孩子都忙着喂动物，骑骆驼。几家小孩都大呼过瘾。环境好，眼睛里充满了绿色，值得一来！', '2014-04-22 12:35:20', '', 'None');
