INSERT INTO `meituan_dongwuyuan` VALUES ('10846', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '整个游玩过程还是蛮高兴的，车行区很爽！  唯一需要抱怨的是路太差了去的时候走的海榆中线，路很差；回来的走的是东线高速，上高速之前的路也很差，但是相对海榆中线来说要好点！在海榆中线没有修好之前还是建议走东线告诉过去吧', '2014-01-17 19:52:24', '', 'None');
