INSERT INTO `meituan_dongwuyuan` VALUES ('10854', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '缺点：路途遥远，路途破烂。动物还算挺多吧！  我就觉得猴子比较好玩好动点。其它的都懒洋洋的反正就是比懒洋洋还要懒就对了。', '2014-01-12 17:03:12', '', 'None');
