INSERT INTO `meituan_dongwuyuan` VALUES ('10870', 'dearnanaren', '8638138', 'https://img.meituan.net/avatar/26f1e9f54c0cc54c59e8700c04873211191655.jpg', '动物挺多的，就是去了火烈鸟餐厅居然还有吃鸵鸟和鹿什么的！不理解！这可是动物园啊！让小朋友情何以堪！还有这个路真是烂的够可以的，底盘挂了几次差点没回去！路不修好休想我再来一次了！', '2014-01-03 12:27:36', '', 'None');
