INSERT INTO `meituan_dongwuyuan` VALUES ('10928', 'SPn15281027237', '44072622', '', '环境很好，空气非常不错，去公园的路超级不好走，根本没有公交车可以过去，从海口市到公园公里49公里左右，一路都是颠簸，门口验证团购的小妹自己没看清楚团购号码竟然说我们不会写字，其他大部分园内员工都无精打采，问下路又感觉骄傲的要死', '2013-11-13 18:31:19', '', 'None');
