INSERT INTO `meituan_fenjiezhou` VALUES ('3', '精灵蜥蜴', '459124662', 'https://img.meituan.net/avatar/aeb6754370dbf46525fdfbdb8e65ace410817.jpg', '三亚应该就属分界洲岛海洋剧场最好了 还便宜 真是物有所值 尤其潜水教练二号三号特别好 本来还以为去潜水会说那些教练不耐烦 这两个教练特别有耐心 我闺七岁 还给冲的白糖水给饼干吃 我妈妈回来最晚教练还让我妈妈玩个够 就是照相的时候照的只有鱼 反正照片也便宜160三张微信发你好多张不过就一种鱼和在水里两种喂海龟三十和海豚握手三十和海豚互动三十 孩子特别喜欢 关键可以自己拍照 在亚特兰蒂斯不让自己拍照 感觉我们这种平民还是适合分界洲岛 沙滩照相特别好看', '2018-10-11 00:32:44', '-1275011871:http://p0.meituan.net/shaitu/3cf5d7457bf9326cfb07a2ceed26bfbd46560.jpg;\n-1274913327:http://p1.meituan.net/shaitu/4002f2086e207ebf4231c1e1af21569f53667.jpg;\n-1275011870:http://p1.meituan.net/shaitu/f0deab2eccaaa9e65af7fbf40dd0fd3250009.jpg;\n-1274913326:http://p1.meituan.net/shaitu/61ab9b3762cbc55e5aed9e4d30856a3345063.jpg;\n-1275011869:http://p1.meituan.net/shaitu/c3ba9b5a2c77955543d2fb0710799a8638142.jpg;\n', 'None');
