INSERT INTO `meituan_fenjiezhou` VALUES ('90', '陶罐小叔', '177977325', '', '景区人员服务态度好但远海潜水另讲，物价很高，位置略有点不方便，海水清澈，海面上基本上看不到垃圾，但是相对于其他三亚的海景并没有很突出的地方。远海潜水就是在离岛不到几百米的地方（第四张图那个棚子就是潜水的地方），而且潜水不提供贵重财物保管，箱子要租用，一次30，态度极不好，换衣服特别不方便，培训也不怎么细，就讲几个手势和排气，但体验还可以，买了鱼食，然后会有超级多的豆娘鱼游过来，在水下往上看可以体验另一个世界，听别人说在水下看到了珊瑚，但本人耳朵有点痛，就没有下潜了，总而言之，潜水过程就水下体验还可以，但其他很差，有点不值这个价钱', '2018-09-25 23:17:38', '-1312229439:http://p1.meituan.net/shaitu/412dbac21e3c63e3eceba547f6d7ea56345497.jpg;\n-1312229438:http://p1.meituan.net/shaitu/996d5579bc1de79c87c8c5599b6b6c6d311926.jpg;\n-1312131902:http://p0.meituan.net/shaitu/0eaaa2f6fed5c1bb7b53b185d87a735b418893.jpg;\n-1296325247:http://p0.meituan.net/shaitu/2cdf208234fd4e9b3bb7ea72a348a9a7442512.jpg;\n-1296325246:http://p1.meituan.net/shaitu/1de422ae893be6d610ddb26d6d8c8a95414641.jpg;\n-1296325245:http://p0.meituan.net/shaitu/f32aef0640ec6f373ad5752b30fd0b86624671.jpg;\n', 'None');
