INSERT INTO `meituan_fenjiezhou` VALUES ('756', 'xBB268853417', '163238598', '', '设施齐全，服务周到，岛上的所有客服人员都很热情，坐观光车中途下车看风景，等下趟车来了上面坐满岛上员工，看有游客，主动全部下车，让我们先乘坐，特别感动，服务理念值得给100个赞?，玩了潜水艇，全潜的美人鱼号，很赞，潜艇里有解说，还会给我们介绍不认识的海洋生物，海洋表演会到游客前面来，进岛出岛很快，最值得说的就是海水颜色了，岸边清澈见底，远处湛蓝明亮。太美了，爬山到景点拍照效果好到爆，观光车40元，到山顶可以喝一杯免费可续杯咖啡，很美味，下次还会再来，而且准备上岛住一天，岛上吃饭，冰淇淋都不贵哦，冰淇淋9元，饭20多一份，椰子15一个', '2018-05-18 23:43:06', '-1439605942:http://p1.meituan.net/shaitu/45c2ba4ff8f82828d90296dbee035559196228.jpg;\n-1439667330:http://p0.meituan.net/shaitu/cf48d8c8ea09b57aa8f6d685c70bfd1d145936.jpg;\n-1439667331:http://p1.meituan.net/shaitu/a25b36491b01e0e1a34af4e1835e83da345728.jpg;\n-1439605941:http://p0.meituan.net/shaitu/56ee48cbdbc1d38744368a0930b153fd269319.jpg;\n', 'None');
