INSERT INTO `meituan_fenjiezhou` VALUES ('1783', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '前面沙滩一般，水比较清，一定要走到后面，有山，到山顶看下面的海才有感觉，整个岛上算很热情，玩了一下摩托艇，千万不要说自己来玩，要说和朋友一大堆来，不然那教练就把你拉好远，感觉很猥琐，还抱我，呕，尽量和朋友一起相跟玩', '2018-01-28 21:39:14', '', 'None');
