INSERT INTO `meituan_fenjiezhou` VALUES ('1800', 'kck101748524', '328922274', '', '岛上各景点服务人员好多东北人，彻底怀疑自己是不是到了东北！门票贵也就罢了，岛上的欢喜堂绝对是个坑！欢喜堂里一帮东北人在忽悠！也不怕佛主降罪！一上去拉着你进佛堂，牵着让你写上堆祝福语，再下来就是一堆费话让你捐钱！恨不得掏光你口袋，幸好我说没现金，还被坑了300快，岛上没玩的，建议大家不要再上当受骗了', '2018-01-23 19:21:57', '', 'None');
