INSERT INTO `meituan_fenjiezhou` VALUES ('2042', 'xqT795337432', '50710882', 'https://img.meituan.net/avatar/1b70f6b7f24b1aa07088d96db555e98657020.jpg', '分界洲岛美团上买门票110，还带75元自助餐一份比较划算。里面消费也没有蜈支洲岛那么高。家庭度假可玩性也更高（重点小朋友）。里面还有木结构的海景房大概600一间，很不错。风景也和蜈支洲岛差不多，沙滩比蜈支洲岛差那么一点。温度也比蜈支洲岛那边低2-3度。没去过蜈支洲岛或分界洲岛旅游的朋友，推荐分界洲岛。', '2017-12-09 16:20:37', '-1843664226:http://p0.meituan.net/shaitu/db826e1f8562456cb958f4256069d6fe50716.jpg;\n-1843664221:http://p1.meituan.net/shaitu/c0c437c9a4f7973f32efb0ffb533b16499492.jpg;\n-1843764636:http://p1.meituan.net/shaitu/9897ec02ebcd815e3a7bf303f273a60f54708.jpg;\n-1843664214:http://p1.meituan.net/shaitu/67373fbbf79487847b5aafb72cfc9023105422.jpg;\n-1843664211:http://p1.meituan.net/shaitu/7388278acd63215d65aea3e9464140ba62584.jpg;\n-1843664210:http://p1.meituan.net/shaitu/e5c49d0f80260550bb807e5c4d98ae31111941.jpg;\n-1843664209:http://p1.meituan.net/shaitu/1b14dc3c3e171098e53a4f336fdb975875066.jpg;\n-1843664205:http://p1.meituan.net/shaitu/237dff6d8843317592549e5277fd636362954.jpg;\n', 'None');
