INSERT INTO `meituan_fenjiezhou` VALUES ('2935', 'rodick777', '158406436', '', '一次说走就走的旅游来到了分界洲岛，岛上的风景非常的美，青山绿水碧海蓝天，120元的美团票包含的午餐在那个悬崖的什么幽兰餐厅吃的自助餐，有十多样菜品，味道还过得去，娱乐项目都是自费，价格不便宜，分界洲适合过来休闲，拍照，慢节奏的游玩，这里的游泳区水比较浅，适合小孩子和带着妹纸戏水，真的想游泳这里感觉不是很过瘾，其他的都非常好，潜水没去玩，听说价格偏高，其实如果想玩，一次把次贵点也无所谓。', '2017-08-07 17:49:48', '-1871775443:http://p1.meituan.net/shaitu/75f932448308cc4cdf947e056d9a5a02232520.jpg;\n-1871073736:http://p0.meituan.net/shaitu/5b327eeaa8ded96567d04a31fbe5fe16183731.jpg;\n-1872042742:http://p0.meituan.net/shaitu/6a1c33e2760ec1d07d6427b655778d13275885.jpg;\n-1871496937:http://p1.meituan.net/shaitu/bf83d0e8f994d9d270aa61211781b6ce174117.jpg;\n-1871144956:http://p0.meituan.net/shaitu/4bd77228e9cca54de8a230ffcee326d3323573.jpg;\n-1871249488:http://p1.meituan.net/shaitu/f5cb110b0dd31633d476ae891ca1b16e285267.jpg;\n-1871496953:http://p1.meituan.net/shaitu/388f35395dbd99e7a45c77a10804c208210209.jpg;\n-1871108420:http://p0.meituan.net/shaitu/28f80e5c6bc3ef34e9d4f5403fc2aad5263920.jpg;\n', 'None');
