INSERT INTO `meituan_fenjiezhou` VALUES ('3536', '清沁淡雅', '32423244', '', '也许是五一小长假吧，岛上人很多，风景在海南岛处处都美美的，因为有蓝天白云椰树海风，是摄影爱好者和拍婚纱照的情侣喜欢的去处，160多元的船及门票，美团135元，如果不玩项目，只是吹吹海风散散步拍拍照就要这么贵的门船票，个人觉得真的偏贵，入岛后各项都是另自费的，岛上的食品价格高点是可以理解的，但希望所点菜品应做到现点现炒。这也是唯护景点名誉的窗口，诚信待客才是为商之道。', '2017-05-02 09:27:38', '-1886180038:http://p0.meituan.net/shaitu/d08d7b592c09f1c556c009c061bfab13246949.jpg;\n-1885641385:http://p0.meituan.net/shaitu/9a1fbfee747867ae8cfadfde12d1453a357508.jpg;\n-1885548033:http://p0.meituan.net/shaitu/45713af56fbd5e8bbbb92088c58068ae521443.jpg;\n-1885687571:http://p0.meituan.net/shaitu/32d49f5a2c2ba90cf8e6a0f9b59e57c3328167.jpg;\n-1885663301:http://p0.meituan.net/shaitu/6b750a66a5809556343efea15eecba03444807.jpg;\n-1886235358:http://p0.meituan.net/shaitu/cbb7a0a519191998c133e1a4abb60980518764.jpg;\n-1885670335:http://p0.meituan.net/shaitu/b555f91770bf3bb13fb3faa1473c6120266261.jpg;\n-1886009314:http://p0.meituan.net/shaitu/60d9061abafb4a08393e78391e5a6099326762.jpg;\n', 'None');
