INSERT INTO `meituan_fenjiezhou` VALUES ('5132', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '分界洲岛风景很美，环境好，空气很清晰，非常适合度假，海洋馆里的海洋动物有海狮，大大的海豚，大大的鲸鲨，大大的海龟，小朋友看到都非常高兴，很喜欢，水上项目也有好多，潜水，摩托艇，都玩了，很刺激，不来就后悔了，值得推荐。赞！', '2016-12-13 18:08:30', '-1903236520:http://p0.meituan.net/shaitu/5010e3241c786b2e554e4e760241f81035993.jpg;\n-1903376832:http://p0.meituan.net/shaitu/e011e52c63e97ac931bcda39d2847ba328076.jpg;\n-1903910802:http://p0.meituan.net/shaitu/fd7175040ce95c1126eb8bd929e0c22730541.jpg;\n-1903477869:http://p1.meituan.net/shaitu/75ea214c0df2d44e0ae92e8353c3044315487.jpg;\n', 'None');
