INSERT INTO `meituan_fenjiezhou` VALUES ('5701', 'Eimamo', '102441746', 'https://img.meituan.net/avatar/acfbde9b6b54b0aff15876716d2a51158515.jpg', '很划算的套餐，但是我是假期去的人好多，天气又热，排队又很久，好在服务人员的态度不错。海豚表演还是值得看的，但是得有位置，原因还是人太多。还有一些海底打捞上来的瓷器……\n总体来说还是不错的，下次可以带家人去玩玩，看看风景，', '2016-10-06 12:59:28', '-1914836241:http://p1.meituan.net/shaitu/d58d4c05d70fd26036399f0b29092c0c48196.jpg;\n-1914840523:http://p1.meituan.net/shaitu/1e440b0568a2158c5d21992a5c1374d639252.jpg;\n', 'None');
