INSERT INTO `meituan_fenjiezhou` VALUES ('6145', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '分界洲岛特别美丽，特别山顶餐厅，棒到无可挑剔，风景打100分，导游小鱼也不错，特别值得购买！车程有点远，自己去实在是太麻烦了，报团一日游，价格实惠，性价比超高，来回车程都需要4个小时，但是那的水质，风景无可挑剔！', '2016-06-14 19:22:31', '-1931338604:http://p0.meituan.net/shaitu/db77b3692c133dec087433cfb04d77bb1051388.jpg;\n-1932158654:http://p0.meituan.net/shaitu/0d2ddefeb8c82df8cc6d38513ed54a80605336.jpg;\n-1931359544:http://p1.meituan.net/shaitu/db1d70b963a3e73aa37c1e43730b5c98426093.jpg;\n', 'None');
