INSERT INTO `meituan_fenjiezhou` VALUES ('6155', 'cHi783875515', '148099399', '', '拜托你们网页上写上买了票两小时之后才能进去，我过去足足等了一个半小时，要不是没赶上猴岛的车，我也不用受这份气，怎么鹿回头公园买了票十分钟就能进你们这2小时，这是逼游客退票吗？规定不能变通一下？实在是忍不了，风景还是不错的', '2016-06-10 18:14:30', '-1937716315:http://p0.meituan.net/shaitu/15e1b5ab11207c1ade500ddf0127ca5f92774.jpg;\n-1937778845:http://p0.meituan.net/shaitu/f3dfb69a7669aa4408c7142d9d07a992144158.jpg;\n-1937603572:http://p0.meituan.net/shaitu/331f62d277ce27c19aef1e96be201f1383517.jpg;\n-1937540380:http://p1.meituan.net/shaitu/924b5b6e198fe39f323d4d16ab42730677999.jpg;\n', 'None');
