INSERT INTO `meituan_fenjiezhou` VALUES ('6472', '余禾1978', '211153960', '', '提前两小时买票即可，凭身份证，手机短信取票，团购能省不少钱。水很清，岛上很美，工作人员服务周到，就是岛上东西都很贵，最好带上些吃的，总之值得一去。那天由于我们人多，疏忽，一个背包忘在沙滩上，离岛之后返回找包，工作人员只许一人返回。包仍然完好无损呆在沙滩。', '2016-02-20 20:36:58', '-2017352673:http://p0.meituan.net/shaitu/0a4a084bd9eb645a3081046d6f6caa7c37453.jpg;\n-2017190424:http://p1.meituan.net/shaitu/0aa79b217648a1065dba4891a500d92445352.jpg;\n-2017260000:http://p1.meituan.net/shaitu/7a6417d2ead094c0f04e37be46b89d4039324.jpg;\n', 'None');
