INSERT INTO `meituan_fenjiezhou` VALUES ('6940', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '环境很不错的，水很清，里面的瞰海别墅环境特别赞，如果有机会和亲密爱人住一晚，听听海，看夕阳，游游泳，潜潜海，超级赞！人也不算多，适合情侣去，就是当天太阳太大了，晒爆了！商业味太浓了，海洋文化馆不错。景点设施维护有点欠奉！', '2015-06-20 16:29:51', '-1967628747:http://p0.meituan.net/shaitu/33cdc92907d1277a61623d27621017d1339759.jpg;\n-1968006130:http://p0.meituan.net/shaitu/da2db78dad472acbede3b6aa7205c36e305593.jpg;\n-1967719769:http://p0.meituan.net/shaitu/e6129979963c99aec27ae1cd06cdcee6538717.jpg;\n-1968006114:http://p1.meituan.net/shaitu/60859caaa412ca1bcd8b9d84c1535aaa389587.jpg;\n-1967719737:http://p1.meituan.net/shaitu/b037f337837307ce49a0d5964ca3869d375309.jpg;\n', 'None');
