INSERT INTO `meituan_fenjiezhou` VALUES ('6996', '小硕20131222', '174725693', '', '很满意啊，导游小沈是东北人，很幽默，因为我们是回民，吃的不方便，小沈也很乐意的给我们在岛上指点到哪里吃，下次还要预定这家旅行社的其他团购一日游，又划算，导游又好，而且分界洲岛很值得一去，很好玩，水很清，到那里买海洋馆的票，还送饮品，后来我们没找到，就没有喝，东西是翻倍的卖，更衣室简陋些，而且储物柜是二十块一次性的，不可以拿东西，大家去的时候可以让导游帮忙拼团自助餐，58一位，所有饮品小吃水果都有，比较划算的', '2015-05-12 09:56:47', '-1926756957:http://p1.meituan.net/shaitu/4749841d74fe01d57470730820a6fe3d204339.jpg;\n-1927298534:http://p0.meituan.net/shaitu/06a59a8130d3aecdb9fc1f6ea1ac3357227726.jpg;\n-1926691707:http://p1.meituan.net/shaitu/8308c0dd687fec9657374e803167c7a6274982.jpg;\n-1927348990:http://p1.meituan.net/shaitu/c7fdd5abdb6fa1286fa4538ce85f9314366992.jpg;\n-1926577044:http://p0.meituan.net/shaitu/bd9ca9a061d659c4f9e6142663f8fa08462684.jpg;\n-1926728329:http://p1.meituan.net/shaitu/e863157534c404160e152fcd4f33e20f355615.jpg;\n-1926723455:http://p0.meituan.net/shaitu/8db2dd3b7286311b86319bfbb9c6306f377875.jpg;\n-1927298518:http://p0.meituan.net/shaitu/a797c82c8a590ac1252e85374f8ba10e254369.jpg;\n', 'None');
