INSERT INTO `meituan_fenjiezhou` VALUES ('7248', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '购票过程非常愉快。就是取票等了许久。购票的朋友最好提前一天预订。越早越好', '2015-02-07 18:50:40', '-1936596628:http://p1.meituan.net/shaitu/b5a8a2a5ee9a29f678ccb883f9133d8737088.jpg;\n-1937186282:http://p0.meituan.net/shaitu/d147356020358743fa3098850d3940ba72930.jpg;\n-1936770865:http://p1.meituan.net/shaitu/3d1846db7d133e9f777116f8859729ca54580.jpg;\n-1936764069:http://p0.meituan.net/shaitu/6b07d5df2be32fa90d1681f20e3d1d6076100.jpg;\n', 'None');
