INSERT INTO `meituan_fenjiezhou` VALUES ('7631', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '自驾去的景区服务很好．到景区忘了关车大灯．回来车打不了火．找景区工作人员帮忙．马上三个工作人员拿来电瓶搭电马上就好．开车回家．非常谢谢分界洲景区工作人员，给N 个赞都不为过．分界洲岛很美．分界洲景区工作人员谢谢你们啦．下次来海南还到分界洲。', '2014-10-13 11:02:15', '-1941146452:http://p1.meituan.net/shaitu/652bcc97ca1dd7f1cdc201bad31ff43697759.jpg;\n-1941720010:http://p1.meituan.net/shaitu/9b93daaa82df0b61ba3462e5cc7fcc22123585.jpg;\n-1941316453:http://p0.meituan.net/shaitu/b27a7c67cc0e0a4217ea0e222a13dd5349947.jpg;\n-1941319101:http://p1.meituan.net/shaitu/6c1149fa0eefa71f60287c574f59d27b89082.jpg;\n-1941849430:http://p1.meituan.net/shaitu/13661f023ed85172397f05c3096978fe61763.jpg;\n-1941319085:http://p0.meituan.net/shaitu/411e9ba778696f93dce8f637087a6e4757842.jpg;\n-1941146436:http://p1.meituan.net/shaitu/ddea603141848bed1dfba64ff32d84a698463.jpg;\n-1941092572:http://p1.meituan.net/shaitu/9fafa01d30dd6b531abec9fbafbf32ab74725.jpg;\n', 'None');
