INSERT INTO `meituan_fenjiezhou` VALUES ('8236', 'newandymn', '20813036', '', '团购价非常划算，比去售票处便宜差不多50％，而且领票超级方便，凭身份证就ok了，速度也极快，还送一杯咖啡或者铁观音，不错不错，推荐一哈！还有就是非常希望能够提供多种组合的项目或者可以自由组合，这样大家就可以将省钱进行到底，哈哈！', '2014-02-12 23:46:58', '', 'None');
