INSERT INTO `meituan_fenjiezhou` VALUES ('8260', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '真心不错，强烈推荐。只要提前半小时把密码和取票人姓名发送页面手机就可以去游客中心取票了。还有赠送的咖啡。边喝咖啡边欣赏碧海蓝天真是太舒服啦', '2014-02-03 23:27:36', '', 'None');
