INSERT INTO `meituan_nanwan` VALUES ('17', 'Ous788284363', '208701377', '', '「跨海索道」第一次坐索道还有点怕，但索道很稳，有的地方要过接口，会有点抖，很刺激，完全不用怕，在索道可以看到很漂亮的海和船。在入口排队的时候会有猴哥合影，有的好看有的不好看，到猴岛以后会有工作人员给看照片，如果要的话一张20，可以帮忙过塑。不要要没关系，个人建议猴食或者零食可以买，因为他们会过来和你拿吃的，但是一定不要让他们看见你从兜里或者包里拿出来，不然他就会掏你兜，男朋友买的花生被一只小猴子掏去了一大半，总之还是挺有意思的', '2018-10-04 22:55:55', '-1283840139:http://p0.meituan.net/shaitu/8a8896de006ea183024b3bac443a5af3876715.jpg;\n-1283738915:http://p0.meituan.net/shaitu/d50433340e10c2b42fd8ea146db834f4470735.jpg;\n-1283840134:http://p1.meituan.net/shaitu/b802b63343364c98172c8d602b67db6b582425.jpg;\n-1283840129:http://p0.meituan.net/shaitu/c48bb0e824b8f658bb7f621252b8f3af896404.jpg;\n-1283738910:http://p0.meituan.net/shaitu/1ef59a3669bd7367ebb8498c51f7a633551799.jpg;\n-1283738909:http://p1.meituan.net/shaitu/5e8288577edad23487fe3cc829065526393962.jpg;\n-1283840128:http://p1.meituan.net/shaitu/b56415c88920aa3a2d8ef44a30ae23ed289443.jpg;\n-1283840127:http://p1.meituan.net/shaitu/711b5411864fca2abbb239278a5b0e48313004.jpg;\n', 'None');
