INSERT INTO `meituan_nanwan` VALUES ('811', 'jushia', '34466372', '', '又贵又不好玩，首先不好停车，买票排队很久，缆车不开，排队等船要很久，不得已又花了60坐私家船，进岛后上山大巴又要排队，也有不排队的方法，就是花钱包车，猴子也没几只，动物表演前还卖书法作品，一看就是大忽悠，出景区还要排队，不排队的方法又是花钱包车，体验最差的景区，不会再起，也不会推荐朋友去', '2018-02-10 22:25:29', '', 'None');
