INSERT INTO `meituan_nanwan` VALUES ('825', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '猴岛很小，但是确实能跟猴子近距离接触，他们也不怕人，但是会抢游客的吃的，所以一定不能让孩子吃东西，排队坐缆车特别坑！排的队几个小时，都是带孩子的还有老人，等的时间比在岛上观看的时间长，坐船会好很多，不至于等那么久，猴岛上下来可以去疍水人家吃个海鲜，但是很贵，可以团购一个相对便宜点的，否则价格也很坑', '2018-02-09 21:14:09', '-1613996465:http://p0.meituan.net/shaitu/88d040c6ca3c3844f318e5ee556db608266637.jpg;\n-1613949772:http://p0.meituan.net/shaitu/b4a8ee6dae67fa661c7b356dc5d995533171390.jpg;\n-1613996464:http://p0.meituan.net/shaitu/d2dec25668378ac4af3a63d8763e1f673479521.jpg;\n-1613949773:http://p0.meituan.net/shaitu/c95d6ad56a0e4ebface38e8a2b8647d5143162.jpg;\n', 'None');
