INSERT INTO `meituan_nanwan` VALUES ('842', 'vTw850382296', '141571534', '', '起早从三亚站坐动车到陵水的，以为早点去能早点回来，谁知道去早了就是排队，还不如中午以后上岛，因为岛太小了，根本在岛上待不了多长时间，基本都是排队的时间比在岛上的时间长，最值得一提的就是索道了，真的很不错，又高又长，我们来回都做的索道，就是索道排队的人太多了', '2018-02-07 17:50:20', '-1684178763:http://p0.meituan.net/shaitu/0a9b3f2e751cb9ad688de9984bcb1ecb370088.jpg;\n-1684178762:http://p0.meituan.net/shaitu/b612b840f27dad32270bd2a3d12c8a57405680.jpg;\n', 'None');
