INSERT INTO `meituan_nanwan` VALUES ('867', 'chunyu花花', '140602572', 'https://img.meituan.net/avatar/80fb7fa2c6a3b4ff788eb873683a9f93228013.jpg', '小岛不大 ，跟父母一起去的 。岛上的猴子实在是太热情了 ，小猴子很可爱 。虽然票价小贵 ，可以选择坐船或者缆车上岛 ，但是缆车还是值得一坐的 。美中不足的是需要排很久的才能排到缆车 。建议尽早去排队，可以选择缆车上岛，坐船返回。', '2018-02-03 16:51:35', '-1789757206:http://p0.meituan.net/shaitu/5e4427d10bbd4612c0df5ef97ad835664560372.jpg;\n', 'None');
