INSERT INTO `meituan_nanwan` VALUES ('2055', 'heyingjudy', '27761278', '', '不错的亲子游，坐缆车海上面的鱼排特别壮观，孩子第一次坐缆车特别高兴，进门就会有有很多猴子在那里自由游玩嬉戏，小猴子实在太可爱了，看了两个猴子表演，相声和杂耍，猴子们太聪明了，还可以和猴子明星拍照，一人10块，犯错误的猴子还要关禁闭，有意思，非常值得带孩子去玩。据说天越来越热，所以猴子没有特别多，要是再多点猴子就更好了。注意一定不要穿太艳丽颜色的衣服，不要对猴子瞪眼，进园区不要打开包包。', '2017-04-28 06:41:28', '-1886800394:http://p1.meituan.net/shaitu/0ea357ee47d0dab794f8ea6d9f77f95c143324.jpg;\n-1886951054:http://p1.meituan.net/shaitu/78d84e35b312c89fdc2829691b15fe88159871.jpg;\n-1886257745:http://p1.meituan.net/shaitu/6433a6306931309ae19e8bd5aefd48fb224328.jpg;\n-1885922928:http://p0.meituan.net/shaitu/b657742613554b01726546d8531c2e5e278184.jpg;\n-1886375061:http://p1.meituan.net/shaitu/0d00c9790c28f7524b14a8a3d1767d7f212769.jpg;\n-1886347725:http://p0.meituan.net/shaitu/dabc9cd98aeab01726e221f3f8fdc24e235981.jpg;\n-1886368391:http://p1.meituan.net/shaitu/63b37d071fbacaf41ec06c6a263ccb22187617.jpg;\n-1886394147:http://p0.meituan.net/shaitu/e00812e647f7c81109ad7d2817ce392b138545.jpg;\n', 'None');
