INSERT INTO `meituan_nanwan` VALUES ('2499', '棋棋0630', '8810584', 'https://img.meituan.net/avatar/b0ac06b48029caf40f7eff50d7a0f81233413.jpg', '电子票取票方便，我们去的早不用排队就取到了票，但是乘坐缆车酒要排队了，缆车要坐，可以看见建水面上的渔排，很壮观！建议单程缆车就可以，人多回来可以坐船，省去排队的时间！岛不大，半天就可以游完。岛上小猴子挺多，超可爱，上岛前买些花生，喂食可以近距离的接触它们！真的别打开包包，一不留神就被抢了！我们在看节目时饿了，拿出点面包自己吃，结果就被洗劫了！节目没那么精彩，幽默剧场好一点，另一个剧场商业性太浓！总之，值得一去！', '2017-02-16 12:42:18', '-1894942588:http://p0.meituan.net/shaitu/4f437904d33bbe226a54d20093c2c73b278280.jpg;\n-1894914808:http://p0.meituan.net/shaitu/e6b4194feb857689923f31238d0a68f2257174.jpg;\n-1895294453:http://p1.meituan.net/shaitu/53791ed8a3236d3e403ed1f901275b64282457.jpg;\n-1895199601:http://p0.meituan.net/shaitu/f9dfb9fcb2d6c2867ff840e7c706e128200105.jpg;\n-1895289213:http://p1.meituan.net/shaitu/91f15a88e0ce4458a3d1d6460c8f530a222118.jpg;\n-1895692490:http://p1.meituan.net/shaitu/41b90e8de62d34ce2ca3acc384dfaf48173747.jpg;\n', 'None');
