INSERT INTO `meituan_nanwan` VALUES ('3218', 'Kaixing125', '18215700', '', '停车不方便，不让开进去，叫停路边，经过本地人停门口要30元停车费，我们停的有点儿远，在第二排没有人收费，太多的人坐缆车，2个小时，个人认为浪费时间精力，不如坐轮船，5分钟，猴岛没有几只猴子，我们下午去的，可能都吃饱了，就看个表演，没有性价比，没有小孩的，不建议去', '2017-01-23 18:51:51', '', 'None');
