INSERT INTO `meituan_nanwan` VALUES ('4066', 'Lynn9207', '223487759', 'https://img.meituan.net/avatar/1169ecf63e801c670a9187a0d3dbd426168242.jpg', '猴岛是我来三亚最喜欢的地方之一，岛上有很多野生猕猴，猴子很可爱一点都不怕人，我是缆车来回的，缆车上能看到很漂亮的海景。门票包含来回缆车和两个表演，表演时间不长但是很有趣。另外去猴岛千万千万不要吃东西，不要从包里拿东西，猴子会抢的。', '2016-05-30 09:35:04', '-2086459184:http://p0.meituan.net/shaitu/f7cfd8476fcf4d96382d643c1c6f1a08116082.jpg;\n-2086396179:http://p1.meituan.net/shaitu/bc642352c348348d7af49d8c1254a15265898.jpg;\n-2086423551:http://p0.meituan.net/shaitu/8bf0909b005010dbac26ceee6ecb2dfa47533.jpg;\n-2086510998:http://p1.meituan.net/shaitu/000a4637160a8d7929001b7c0924af64137767.jpg;\n-2086419992:http://p0.meituan.net/shaitu/034363c5648b38c5cd6585c45b0fe1ae114576.jpg;\n', 'None');
