INSERT INTO `meituan_nanwan` VALUES ('4069', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺好的。自己导航开车去的。但可以后换票也比较方便，猴子跟可爱，但就是园区没太潮湿了，待了没多久就出来了，做索道挺过瘾的。', '2016-05-27 19:19:50', '-2000235949:http://p0.meituan.net/shaitu/a335863650eeec0bc9286be84f2cc83a911164.jpg;\n-2000067580:http://p1.meituan.net/shaitu/6b1549b4c7c34f2a6568eada3cdf9f9d454992.jpg;\n-2000301345:http://p1.meituan.net/shaitu/148bb0d48796fe62994088600081c7d5644149.jpg;\n-2000243397:http://p1.meituan.net/shaitu/47091378c9ca1d5337809aefbe9cfbb4347910.jpg;\n-2000209401:http://p0.meituan.net/shaitu/d699ffb13fa894f495251298cbddf035443161.jpg;\n-2000436450:http://p0.meituan.net/shaitu/52e5c754122b8af861e17f84fb356960382047.jpg;\n-2000241459:http://p1.meituan.net/shaitu/f317156d1a89261de867b315940ff6e6633532.jpg;\n-2000587214:http://p1.meituan.net/shaitu/3a1c1342afa8824d6284f6e4d8303914601070.jpg;\n', 'None');
